import { useState, useEffect } from "react";
import Shimmer from "./Shimmer";

const RestaurantMenu = () => {
  const [resInfo, setResInfo] = useState(null);
  useEffect(() => {
    fetchMenu();
  }, []);

  const fetchMenu = async () => {
    const data = await fetch(
      "https://www.swiggy.com/dapi/menu/pl?page-type=REGULAR_MENU&complete-menu=true&lat=18.5204303&lng=73.8567437&restaurantId=25396"
    );

    const json = await data.json();
    console.log("restaurant details: ", json?.data); //cards[0]?.card?.card?.info
    setResInfo(json.data);
  };

  if (resInfo === null) return <Shimmer />;

  const { name, cuisines, costForTwoMessage } =
    resInfo.cards[0]?.card?.card?.info;

  const { itemCards } =
    resInfo?.cards[2]?.groupCard.cardGroupMap?.REGULAR.cards[1].card.card;

  return (
    <div className="res-menu">
      <h1>{name}</h1>
      <p>{costForTwoMessage}</p>
      <h2>{cuisines.join(", ")}</h2>
      <ul>
        <li>{itemCards.info.name}</li>
        {/* <li>{resInfo.cards[1].card.card.info.name}</li>
        <li>{resInfo.cards[2].card.card.info.name}</li> */}
        <li>Biryani</li>
        <li>Burger</li>
        <li>Diet Coke</li>
      </ul>
    </div>
  );
};

export default RestaurantMenu;
